//     var startHour = start.toString().substring(11, 13);

//     var startMinute = start.toString().substring(14, 16);

//     startHour = (parseInt(startHour) * 60);
//     startMinute = parseInt(startMinute);
//     var startTime = startHour + startMinute;

//     var endHour = end.toString().substring(11, 13);
// 	var endMinute = end.toString().substring(14, 16);

// 	endHour = (parseInt(endHour) * 60);
