catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var ga = new GlideAjax('AlreadyBooked');
    ga.addParam('sysparm_name', 'sendBookings');
    ga.addParam('sysparm_date', g_form.getValue('day'));
    ga.addParam('sysparm_room', g_form.getValue('room_name'));
    ga.getXML(callback);

    function callback(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');

        answer = JSON.parse(answer);
// 		alert(answer);
		g_form.showFieldMsg('day', 'Reservations:');
        for (var i = 0; i < answer.length; i++) {

		g_form.showFieldMsg('day', answer[i])
        }
    }
}
