sys_script_include.script:
var OpeningHours = Class.create();
OpeningHours.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    sendOpeningHours: function() {
        var date = this.getParameter('sysparm_date');
        var gr = new GlideRecord('x_950986_work_hub_opening_hours');
        gr.addQuery('date', date.date);
		gs.info(date.date);
        gr.query();
        if (gr.next()) {
            return gr.opening_time;
        }
    },

    type: 'OpeningHours'
});
